  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all image_tests as @image_tests" do
      image_test = ImageTest.create! valid_attributes
      get :index
      assigns(:image_tests).should eq([image_test])
    end
  end

  describe "GET show" do
    it "assigns the requested image_test as @image_test" do
      image_test = ImageTest.create! valid_attributes
      get :show, :id => image_test.id.to_s
      assigns(:image_test).should eq(image_test)
