  end

  describe "GET new" do
    it "assigns a new image_test as @image_test" do
      get :new
      assigns(:image_test).should be_a_new(ImageTest)
    end
  end

  describe "GET edit" do
    it "assigns the requested image_test as @image_test" do
      image_test = ImageTest.create! valid_attributes
      get :edit, :id => image_test.id.to_s
      assigns(:image_test).should eq(image_test)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new ImageTest" do
        expect {
          post :create, :image_test => valid_attributes
        }.to change(ImageTest, :count).by(1)
      end

      it "assigns a newly created image_test as @image_test" do
        post :create, :image_test => valid_attributes
        assigns(:image_test).should be_a(ImageTest)
        assigns(:image_test).should be_persisted
      end

      it "redirects to the created image_test" do
        post :create, :image_test => valid_attributes
        response.should redirect_to(ImageTest.last)
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved image_test as @image_test" do
