        assigns(:image_test).should be_a_new(ImageTest)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        ImageTest.any_instance.stub(:save).and_return(false)
        post :create, :image_test => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested image_test" do
        image_test = ImageTest.create! valid_attributes
        # Assuming there are no other image_tests in the database, this
